import UIKit

class SplashWireframe: BaseWireframe {
    
    // TODO: Use tuples for extras if you have more than once parameters
    func show(transitionType: WireframeTransitionType, delegate: SplashPresenterDelegateProtocol? = nil, extras: Any? = nil) {
        // TODO: Choose related storyboard
        let viewController: SplashViewController = UIStoryboard.Main.instantiateViewController()
        
        // TODO: Inteactor Dependencies
        let interactor = SplashInteractor(networkService: NetworkService.sharedInstance, firebaseService: FirebaseService.sharedInstance)
        
        let presenter = SplashPresenter(viewController: viewController, interactor: interactor, delegate: delegate, extras: extras)
        
        viewController.presenter = presenter
        interactor.presenter = presenter
        
        changeView(transationType: transitionType, viewController: viewController)
    }
    
}
